select * from product_detail_info 
where

    pi_name like #{keyword} or mfi_name like #{keyword} or si_name like #{keyword}


    pi_name like #{keyword}


    si_name like #{keyword}


    mfi_name like #{keyword}

order by pi_seq desc
limit 12 offset #{offset}

select count(*) from product_detail_info 
where

    pi_name like #{keyword} or mfi_name like #{keyword} or si_name like #{keyword}


    pi_name like #{keyword}


    si_name like #{keyword}


    mfi_name like #{keyword}

select a.*, b.mi_seq, b.mi_name

, c.rrh_mi_seq is null as report_able

from
review_info a left outer join member_info b 
on a.ri_mi_seq =b.mi_seq

left outer join (
	select * from review_report_history
    where rrh_mi_seq = #{mi_seq}
) c
on a.ri_seq = c.rrh_ri_seq

where ri_pi_seq = #{pi_seq}
order by ri_seq desc 
limit 10 offset #{offset}

select count(*) from review_info a left outer join member_info b 
on a.ri_mi_seq =b.mi_seq
where ri_pi_seq = #{pi_seq}
